import React from 'react';
import YouTube from 'react-youtube';

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      videoList: [
        "ONat6oXo_gg",
        "2g811Eo7K8U",
      ],
      currentVideo: '5mwlU71S7TA',
    }
  }

  randomize = () => {
    if(this.state.videoList.length > 0)
    {
    var items = this.state.videoList
    var item = items[Math.floor(Math.random()*items.length)];
    this.setState({currentVideo: item})
    this.state.videoList.splice(this.state.videoList.indexOf(item), 1);
    }
    else
    {
      this.setState({currentVideo: "gocwRvLhDf8"})
    }
  }

  render() {
    const opts = {
      height: '390',
      width: '640',
      playerVars: {
        // https://developers.google.com/youtube/player_parameters
        autoplay: 1,
      },
    };

    return  (
      <div>
        <YouTube videoId={this.state.currentVideo} opts={opts} onReady={this._onReady} />;
        <a target= "_blank"
        href='https://www.digipen.edu/sites/default/files/public/img/news/05-body/corey-bowen-his-magical-job-designing-magic-gathering-cards-body1.jpg'>Cards Download</a>
        <button onClick={this.randomize}>Randomize</button>
        Mission Statement goes here.
      </div>
    )
  }

  _onReady(event) {
    // access to player in all event handlers via event.target
    // event.target.pauseVideo();
  }
}
